package src.aims;public class Cart {    public static final int MAX_NUMBER_ORDERED = 20;    private DigitalVideoDisc itemOrdered[] = new DigitalVideoDisc[MAX_NUMBER_ORDERED];    private int qtyOrdered = 0;    public float totalCost() {        float totalCost = 0;        for (DigitalVideoDisc d: itemOrdered){            if(d!=null){                totalCost += d.getCost();            }        }        return totalCost;    }    public int addDigitalVideoDisc(DigitalVideoDisc disc) {        if ( this.qtyOrdered< Cart.MAX_NUMBER_ORDERED){            this.itemOrdered[this.qtyOrdered] = disc;            this.qtyOrdered +=1;            System.out.println("The disc has been add.");            return 1;        } else {            System.out.println("Your cart is full");        }        return 0;    }    public int addDigitalVideoDisc(DigitalVideoDisc [] dvdList){        int countAdded = 0;        for (int i =0;i<dvdList.length;i++){            countAdded += addDigitalVideoDisc(dvdList[i]);        }        return countAdded;    }    public int addDigitalVideoDisc(DigitalVideoDisc dvd1,DigitalVideoDisc dvd2){        int countAdded = 0;        countAdded += addDigitalVideoDisc(dvd1);        countAdded += addDigitalVideoDisc(dvd2);        return countAdded;    }    public void setQtyOrdered(int qtyOrdered){        this.qtyOrdered = qtyOrdered;    }    public void removeDigitalVideoDisc(DigitalVideoDisc disc){        if(qtyOrdered>0){            for(int i =0;i<qtyOrdered;i++){                if (disc.equals(itemOrdered[i])){                        itemOrdered[i] = null;                        setQtyOrdered(qtyOrdered - 1);                        break;                }            }        }    }    public void search(String title){        int count = 0;        for (DigitalVideoDisc d:itemOrdered) {            if (d != null && d.getTitle() == title) {                count++;                System.out.println("Found a DVD of the title: " + d);            }        }            if (count == 0){                System.out.println("Cannot find any DVD of the title!");            }    }    public void search(int id){        int count = 0;        for (DigitalVideoDisc d:itemOrdered) {            if (d!=null && d.getID() == id){                count ++;                System.out.println("Found a DVD of the ID: "+d);            }        }        if (count == 0){            System.out.println("Cannot find any DVD of the ID!");        }    }    public void print() {        System.out.println("***********************CART***********************");        System.out.println("Ordered Items:");        int index = 0;        for (DigitalVideoDisc d: itemOrdered){            if (d!= null) {                index++;                System.out.println(index + "." + d);            }        }        System.out.println("Total cost: " + this.totalCost());        System.out.println("**************************************************");    }}